import React, { useState } from "react";
// import "./App.scss";
import Input from "../Input/Input";
import Button from "../Button/Button";
import Results from "../Results/Results";
import { api } from "../../utils/api";

function App() {
    const [email, setEmail] = useState("");
    const [number, setNumber] = useState("");
    const [isFound, setIsFound] = useState("false");

    const submitForm = () => {
        api.submit({ email, number })
            .then((res) => console.log(res))
            .catch(setIsFound(false));
    };

    const preventRefresh = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
    };
    return (
        <main className="app">
            <form
                className="app__form"
                onSubmit={(event) => preventRefresh(event)}
            >
                <div className="app__wrapper">
                    <Input label="Email" handleChange={setEmail} val={email} />
                    <Input
                        label="Number"
                        handleChange={setNumber}
                        val={number}
                    />
                </div>
                <Button label="Submit" handleSubmit={submitForm} />
            </form>

            <Results />
        </main>
    );
}

export default App;
