import React, { useState } from "react";
// import "./App.scss";
import Input from "../Input/Input";
import Button from "../Button/Button";
import Results from "../Results/Results";
import { api } from "../../utils/api";
import Error from "../Error/Error";

function App() {
    const [email, setEmail] = useState("");
    const [number, setNumber] = useState("");
    const [isFound, setIsFound] = useState(true);
    const [error, setError] = useState("");
    const [apiRes, setApiRes] = useState([]);

    const submitForm = () => {
        api.submit({ email, number })
            .then((res) => {
                setApiRes(res);
                setIsFound(true);
            })
            .catch((err) => {
                console.log(err);
                setIsFound(false);
            });
    };

    const preventRefresh = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
    };

    const handleNumber = (event) => {
        const formattedValue = event.target.value.replace(/[^0-9]/g, ""); // Remove non-numeric characters
        setNumber(formattedValue);
    };

    return (
        <main className="app">
            <form
                className="app__form"
                onSubmit={(event) => preventRefresh(event)}
            >
                <div className="app__wrapper">
                    <Input
                        label="Email"
                        handleChange={setEmail}
                        val={email}
                        isRequired={true}
                    />
                    <Input
                        label="Number"
                        handleChange={handleNumber}
                        val={number}
                        isRequired={false}
                    />
                </div>
                <Button label="Submit" handleSubmit={submitForm} />
            </form>

            {isFound ? (
                <Results data={apiRes} />
            ) : (
                <Error label="User not found"></Error>
            )}
        </main>
    );
}

export default App;
