import React, { useState } from "react";
import Input from "../Input/Input";
import Button from "../Button/Button";
import Results from "../Results/Results";
import { api } from "../../utils/api";
import Loader from "../Loader/Loader";

function App() {
    const [email, setEmail] = useState("");
    const [number, setNumber] = useState("");
    const [isFound, setIsFound] = useState(true);
    const [apiRes, setApiRes] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [firstRender, setFirstRender] = useState(true);

    const submitForm = () => {
        setFirstRender(false);
        setIsLoading(true);
        api.submit({ email, number: number.replace(/-/g, "") })
            .then((res) => {
                setIsLoading(false);
                setApiRes(res);
                setIsFound(true);
            })
            .catch((err) => {
                console.log(err);
                setIsLoading(false);
                setIsFound(false);
            });
    };

    const preventRefresh = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
    };

    const handleNumberChange = (value: string) => {
        const input = value.replace(/-/g, "");
        const formattedInput = input.replace(/(\d{3})(\d)/, "$1-$2");
        setNumber(formattedInput);
    };

    return (
        <main className="app">
            <form
                className="app__form"
                onSubmit={(event) => preventRefresh(event)}
            >
                <div className="app__wrapper">
                    <Input
                        label="Email"
